public class Controller
{
    private readonly ICalculationService _calculationService;
	
    public Controller(ICalculationService calculationService)
    {
        _calculationService = calculationService ?? throw new ArgumentNullException(nameof(calculationService));
    }
	
    public decimal SumValues(decimal valueA, decimal valueB) => _calculationService.Sum(valueA, valueB);
}

class Program
{
    static void Main(string[] args)
    {
        var controller = new Controller(new calculationService());
        decimal result = controller.SumValues(4 , 6);
        Console.WriteLine(result); // Print value: 10
    }
}

